class Tweet {

	int idx;
	int userId;
	int tweetId;
	int time;

	public Tweet(int tweetId, int time, int idx, int userId) {

		this.tweetId = tweetId;
		this.time = time;
		this.idx = idx;
		this.userId = userId;
	}

}

class Twitter {

	int time;
	HashMap<Integer, HashSet<Integer>> followerMap;
	HashMap<Integer, List<Tweet>> tweetMap;

	/** Initialize your data structure here. */
	// O(1)
	public Twitter() {

		time = 0;
		followerMap = new HashMap<>();
		tweetMap = new HashMap<>();
	}

	/** Compose a new tweet. */
	// O(1)
	public void postTweet(int userId, int tweetId) {

		List<Tweet> tweets = tweetMap.get(userId);
		if (tweets == null) {
			tweets = new ArrayList<>();
			Tweet tweet = new Tweet(tweetId, time++, 0, userId);
			tweets.add(tweet);
			tweetMap.put(userId, tweets);
		} else {
			Tweet tweet = new Tweet(tweetId, time++, tweets.size(), userId);
			tweets.add(tweet);
		}
	}

	/**
	 * Retrieve the 10 most recent tweet ids in the user's news feed. Each item in
	 * the news feed must be posted by users who the user followed or by the user
	 * herself. Tweets must be ordered from most recent to least recent.
	 */
	// O(logn)
	public List<Integer> getNewsFeed(int userId) {

		PriorityQueue<Tweet> heap = new PriorityQueue<>(new Comparator<Tweet>() {
			@Override
			public int compare(Tweet o1, Tweet o2) {
				return o2.time - o1.time;
			}
		});

		List<Tweet> tweets = tweetMap.get(userId);
		if (tweets != null)
			heap.offer(tweets.get(tweets.size() - 1));

		HashSet<Integer> followees = followerMap.getOrDefault(userId, new HashSet<>());
		for (int followee : followees) {
			tweets = tweetMap.get(followee);
			if (tweets != null)
				heap.add(tweets.get(tweets.size() - 1));
		}

		List<Integer> ans = new ArrayList<>();
		int size = 0;
		while (!heap.isEmpty() && size < 10) {

			size++;
			Tweet tweet = heap.poll();
			int idx = tweet.idx, followeeId = tweet.userId;
			ans.add(tweet.tweetId);
			tweets = tweetMap.get(followeeId);
			if (tweets != null && idx - 1 >= 0)
				heap.add(tweets.get(idx - 1));
		}

		return ans;
	}

	/**
	 * Follower follows a followee. If the operation is invalid, it should be a
	 * no-op.
	 */
	// O(1)
	public void follow(int followerId, int followeeId) {

		HashSet<Integer> followees = followerMap.get(followerId);
		if (followees == null) {
			followees = new HashSet<>();
			followees.add(followeeId);
			followerMap.put(followerId, followees);
		} else
			followees.add(followeeId);
	}

	/**
	 * Follower unfollows a followee. If the operation is invalid, it should be a
	 * no-op.
	 */
	// O(1)
	public void unfollow(int followerId, int followeeId) {

		HashSet<Integer> followees = followerMap.get(followerId);
		if (followees == null)
			return;
		followees.remove(followeeId);
		if (followees.size() == 0)
			followerMap.remove(followerId);
	}
}

