class Twitter() {

    // A Hashmap of the list of the tweets for each user
    // Each tweet is stored as a pair: The running number of the tweet and the tweet itself 
    private val tweets = HashMap<Int, MutableList<Pair<Int, Int>>>(0)
    // Hashmap of follower to list of followees
    private val followers = HashMap<Int, MutableSet<Int>>()
    // Running number of tweet
    private var tweetOrder = 0

    fun postTweet(userId: Int, tweetId: Int) {
        // Add the tweet to the userId's list of tweets.

        // The list of the userId's tweets is fetched from the map.
        // If this is the userId's first tweet then an empty list is created.
        // Then "also" performs the add to the list and, finally, the entry is placed in the Hashmap. This placing in the Hashmap is to cover the case where this is the first. 

        tweets[userId] =  tweets.getOrDefault(userId, mutableListOf()).also{it.add(Pair(tweetOrder++,tweetId))}
    }

    /** Retrieve the 10 most recent tweet ids in the user's news feed. Each item in the news feed must be posted by users who the user followed or by the user herself. 
     * Tweets must be ordered from most recent to least recent. */
    fun getNewsFeed(userId: Int): List<Int> {
        // Here we will create a list of the last 10 of the userId's tweets plus the last 10 of each of the userId's followees.
        // This will ensure we always get the last 10 in total.

        val result = mutableListOf<Pair<Int, Int>>()

        // First add to the result list the last 10 tweets by this userId.
        // To do this, we sort the list in descending order by the first field in the pair which is the running id of the tweet and take the first 10 entries
        // Get or default ensures that we will get a list even if the userId does not have any tweets.
        result.addAll(tweets.getOrDefault(userId, mutableListOf()).sortedByDescending { it.first }.take(10))

        // Now do the same for all the users that this userId is following
        followers[userId]?.forEach { result.addAll(tweets.getOrDefault(it, mutableListOf()).sortedByDescending { it.first }.take(10))}

        // Finally, take the list we have. It is a list of Pairs of all the last 10 tweets of the userId's and the userid's followees. 
        // First field is running number of tweet and second is the tweet id itself.
        // Sort it by the running number id in descending order and take the first 10.
        // Using map we tell it to replace each entry of the list with the value within the {}. In this case, the second field .
        return result.sortedByDescending{it.first}.take(10).map{it.second}
    }

    /** Follower follows a followee. If the operation is invalid, it should be a no-op. */
    fun follow(followerId: Int, followeeId: Int) {
        // Don't add follow to self!
        if (followerId != followeeId) {
            // Add the followee to the followers set. 
            // "getOrDefault" ensures that we have at least an empty set
            followers[followerId] = followers.getOrDefault(followerId, mutableSetOf()).also { it.add(followeeId) }
        }

    }

    /** Follower unfollows a followee. If the operation is invalid, it should be a no-op. */
    fun unfollow(followerId: Int, followeeId: Int) {
        // Remove the followee from the followers list. 
        // "getOrDefault" ensures that we have at least an empty set. This way we don't need to check if the user already has some followees
        followers.getOrDefault(followerId, mutableSetOf()).also {it.remove(followeeId)}
    }

}

