class Twitter {

    HashMap<Integer, Set<Tweet>> tweetMap; // userId, Set<Tweet> 
    HashMap<Integer, Set<Integer>> fanMap; // userId, Set<Followers> 
    int timeStamp;

    /** Initialize your data structure here. */
    public Twitter() {
        this.tweetMap = new HashMap<>();
        this.fanMap = new HashMap<>();
        this.timeStamp = 0;
    }

    /** Compose a new tweet. */
    public void postTweet(int userId, int tweetId) {
        if (!tweetMap.containsKey(userId)) tweetMap.put(userId, new HashSet<>());
        Tweet newTweet = new Tweet(tweetId, timeStamp++);
        tweetMap.get(userId).add(newTweet);
    }

    /** Retrieve the 10 most recent tweet ids in the user's news feed. Each item in the news feed must be posted by users who the user followed or by the user herself. Tweets must be ordered from most recent to least recent. */
    public List<Integer> getNewsFeed(int userId) {
        List<Integer> res = new ArrayList<>();
        PriorityQueue<Tweet> recentTweets = new PriorityQueue<>(Comparator.comparingInt(Tweet::getTimeStamp).reversed());

        // Get user's tweets and put in recentTweets
        if (tweetMap.containsKey(userId)) {
            recentTweets.addAll(tweetMap.get(userId));
        }

        // Get user's follower tweets and put in recentTweets
        if (fanMap.containsKey(userId)) {
            for (int follower: fanMap.get(userId)) {
                if (tweetMap.containsKey(follower)) {
                    recentTweets.addAll(tweetMap.get(follower));
                }
            }
        }

        while (!recentTweets.isEmpty()) {
            res.add(recentTweets.poll().getTweetId());
            if (res.size() == 10) break;
        }

        return res;
    }

    /** Follower follows a followee. If the operation is invalid, it should be a no-op. */
    public void follow(int followerId, int followeeId) {
        if (followerId == followeeId) return;
        if (!fanMap.containsKey(followerId)) fanMap.put(followerId, new HashSet<>());
        fanMap.get(followerId).add(followeeId);
    }

    /** Follower unfollows a followee. If the operation is invalid, it should be a no-op. */
    public void unfollow(int followerId, int followeeId) {
        if (followerId == followeeId || !fanMap.containsKey(followerId)) return;
        fanMap.get(followerId).remove(followeeId);
    }
}
