class Twitter {
    int time;
    int[] tweets; // all the tweets posted belongs to which user
    User[] user; //all users
    class User{
        Set<Integer> following, followers; 
        Set<int[]> selfPosted, posts; // all posts and selfposts //posts sorted in descending order 0-tweetId 1-time
        User(int userId){
            this.posts = new TreeSet<>((a,b) -> (b[1] - a[1]));
            this.selfPosted = new HashSet<>();
            this.following = new HashSet<>();
            this.followers = new HashSet<>();
            this.following.add(userId);
        }
    }
    public Twitter() {
        this.tweets = new int[10001];
        this.user = new User[501];
    }

    public void postTweet(int userId, int tweetId) {
        tweets[tweetId] = userId;

        if(user[userId] == null)
            user[userId] = new User(userId);

        User currUser = user[userId];
        int[] posted = new int[]{tweetId, time++};
        currUser.posts.add(posted);        // adding to all post
        currUser.selfPosted.add(posted); // adding to self post

        for(int follower : currUser.followers)// adding for all followers
            user[follower].posts.add(posted);

    }

    public List<Integer> getNewsFeed(int userId) {
        List<Integer> feed = new ArrayList<>(10);
        if(user[userId] == null)
            return feed;

        for(int[] post : user[userId].posts){
            int currUser = tweets[post[0]]; //get user who posted
            if(user[userId].following.contains(currUser)) // see if following that user
                feed.add(post[0]);
            if(feed.size() == 10)
                break;
        }
        return feed;
    }

    public void follow(int followerId, int followeeId) {
        User follow = user[followeeId], follower = user[followerId];
        if(follower == null)
            follower = new User(followerId);
            user[followerId] = follower;
        if(follow == null){
            follow = new User(followeeId);
            user[followeeId] = follow;
        }

        follower.following.add(followeeId);
        follower.posts.addAll(follow.selfPosted); // new follower so all selfposted in followers post
        follow.followers.add(followerId);
    }

    public void unfollow(int followerId, int followeeId) {
        User follow = user[followeeId], follower = user[followerId];
        follower.following.remove(followeeId);
        follow.followers.remove(followerId);
    }
}

