            npq.pop();
            int user=it[2];
            int tweet=it[1];
            if(user==userId || mp[userId].find(user)!=mp[userId].end())
            {
                ans.push_back(tweet);
                n--;
            }
        }
        return ans;
    }

    void follow(int followerId, int followeeId) {
        mp[followerId].insert(followeeId);
    }

    void unfollow(int followerId, int followeeId) {
        mp[followerId].erase(followeeId);
    }
