        if userId in self.usersTweets :
            for tweet in self.usersTweets[userId] :
                userNewsFeed.append(tweet)
        #get the tweet of users who the user followed.
        if userId in self.userFollowing :
            for f in self.userFollowing[userId] :
                if f in self.usersTweets :
                    userNewsFeed+=self.usersTweets[f]
        #sort the tweets by time (10 most recent tweet)           
        userNewsFeed = heapq.nsmallest(len(userNewsFeed),userNewsFeed)[0:10]
