class Twitter {

    Map<Integer, Set<Integer>> following;
    Map<Integer, List<Tweet>> feed;
    int time;

    public Twitter() {
        this.following = new HashMap<>();
        this.feed = new HashMap<>();
        this.time = 0;
    }

    public void postTweet(int userId, int tweetId) {
        if(isNewUser(userId)){
            addNewUser(userId);
        }

        feed.get(userId).add(new Tweet(tweetId, time));
        ++time;
        return;
    }

    public List<Integer> getNewsFeed(int userId) {    
        if(isNewUser(userId)){
            addNewUser(userId);
        }

        // Get all the following people tweets and put them in PQ for getting latest feed order
        PriorityQueue<Tweet> minHeap = new PriorityQueue<Tweet>((a, b) -> b.timeStamp - a.timeStamp);
        for(Integer followingId : following.get(userId)){
            if(feed.containsKey(followingId)){
                for(Tweet tweet : feed.get(followingId)){
                    minHeap.offer(tweet);
                }
            }
        }

        List<Integer> userFeed = new ArrayList<Integer>();
        while(userFeed.size() < 10 && !minHeap.isEmpty()){
            userFeed.add(minHeap.poll().tweetId);
        }

        return userFeed;
    }

    public void follow(int followerId, int followeeId) {
        if(isNewUser(followerId)){
            addNewUser(followerId);
        }

        following.get(followerId).add(followeeId);
        return;
    }

    public void unfollow(int followerId, int followeeId) {
        if(!isNewUser(followerId)){
            following.get(followerId).remove(followeeId);
        }
        return;
    }

    public boolean isNewUser(int userId){
        return !following.containsKey(userId);
    }

    public void addNewUser(int userId){
        // create new following List
        following.put(userId, new HashSet<Integer>());
        following.get(userId).add(userId);

        // create new feed list
        feed.put(userId, new ArrayList<Tweet>());
    }
}

