    void unfollow(int followerId, int followeeId) {
        followee[followerId].erase(followeeId);
    }

    void printNewsFeed(int userId) {
        vector<int> newsFeeds = getNewsFeed(userId);
        for(int newsFeed : newsFeeds) cout<<newsFeed<<" ";
