class Twitter:

    def __init__(self):
        self.time = 0
        self.followees = collections.defaultdict(set)
        self.tweets = collections.defaultdict(list)#[time,tweets id]

    def postTweet(self, userId: int, tweetId: int) -> None:
        self.tweets[userId].append([self.time,tweetId])
        self.time -= 1


    def getNewsFeed(self, userId: int) -> List[int]:
        self.followees[userId].add(userId)
        minHeap = []
        rst = []
        # append the most recent tweet of every followee
        for f in self.followees[userId]:
            if f in self.tweets:
                index = len(self.tweets[f]) - 1
                time, twid = self.tweets[f][index]
                minHeap.append([time,twid,f,index-1])
        heapq.heapify(minHeap)
        while minHeap and len(rst) < 10:#use and but not or
            time,twid,f,index = heapq.heappop(minHeap)
            rst.append(twid)
            if index >= 0:
                time, twid = self.tweets[f][index]
                heapq.heappush(minHeap,[time,twid,f,index-1])
        return rst       

    def follow(self, followerId: int, followeeId: int) -> None:
        self.followees[followerId].add(followeeId)


    def unfollow(self, followerId: int, followeeId: int) -> None:
        if followeeId in self.followees[followerId]:
            self.followees[followerId].remove(followeeId)
