#define pii pair<int,int>
class Twitter {
    int time;
    unordered_map<int,unordered_set<int>> followers;
    unordered_map<int,set<pii>> tweets; //userid -> (time, tweetid)'s
public:
    Twitter() {
        time = 0;
    }

    void postTweet(int userId, int tweetId) {
        set<pii> &t = tweets[userId];
        t.insert({++time,tweetId});
        if(t.size() > 10) t.erase(t.begin()); //only store top 10 tweets
    }

    vector<int> getNewsFeed(int userId) {
        vector<int> posts;
        priority_queue<pii,vector<pii>,greater<pii>> p; //min heap to get top 10 latest tweets
		//store self tweets
        for(auto &y: tweets[userId]){
            p.push(y);
        }
		//store followers tweets
        for(auto &x: followers[userId]){
            for(auto &y: tweets[x]){
                p.push(y);
                if(p.size() > 10) p.pop(); //only maintain top 10 tweets
            }
        }
        while(p.size()){
            posts.push_back(p.top().second);
            p.pop();
        }
        reverse(posts.begin(),posts.end()); //tweets should be return from latest -> old
        return posts;
    }

    void follow(int followerId, int followeeId) {
        followers[followerId].insert(followeeId);
    }

    void unfollow(int followerId, int followeeId) {
        followers[followerId].erase(followeeId);
    }
};
