from typing import List, Dict, Optional
import heapq


class Tweet:

    _timestamp = 0

    __slots__ = ["id", "timestamp", "next"]

    def __init__(self, tweet_id: int) -> None:

        self.id = tweet_id
        self.timestamp = self._get_timestamp() 
        self.next: Optional[Tweet] = None

    @classmethod
    def _get_timestamp(cls) -> int:

        # we decrement timestamp to retrieve the most
        # recent tweet first from min heap
        cls._timestamp -= 1
        return cls._timestamp

    def __lt__(self, other: "Tweet") -> bool:

        return self.timestamp < other.timestamp


class User:

    __slots__ = ["id", "tweets", "following"]

    def __init__(self, user_id: int) -> None:

        self.id = user_id
        self.tweets: Optional[Tweet] = None
        self.following = set()

    def follow(self, user_id: int) -> None:

        self.following.add(user_id)

    def unfollow(self, user_id: int) -> None:

        self.following.discard(user_id)

    def post_tweet(self, tweet_id: int) -> None:

        tweet = Tweet(tweet_id)
        tweet.next = self.tweets
        self.tweets = tweet


class Twitter:

    def __init__(self, news_feed_len: int = 10) -> None:

        self._users: Dict[int, User] = {}     
        self._news_feed_len = news_feed_len

    def _get_or_create_user(self, user_id) -> User:

        if user_id not in self._users:
            user = User(user_id)
            self._users[user_id] = user

        return self._users[user_id] 

    def postTweet(self, user_id: int, tweet_id: int) -> None:

        user = self._get_or_create_user(user_id)
        user.post_tweet(tweet_id)

    def getNewsFeed(self, user_id: int) -> List[int]:

        user = self._get_or_create_user(user_id)
        news_feed = []
        recent_tweets: List[Tweet] = []

        for followee_id in (user.following | {user.id}):

            user = self._get_or_create_user(followee_id)

            if user.tweets:
                heapq.heappush(recent_tweets, user.tweets)

        while recent_tweets and len(news_feed) < self._news_feed_len:

            tweet = heapq.heappop(recent_tweets)

            if tweet.next:
                heapq.heappush(recent_tweets, tweet.next)

            news_feed.append(tweet.id)

        return news_feed

    def follow(self, follower_id: int, followee_id: int) -> None:

        user = self._get_or_create_user(follower_id)
        user.follow(followee_id)

    def unfollow(self, follower_id: int, followee_id: int) -> None:

        user = self._get_or_create_user(follower_id)
        user.unfollow(followee_id) 

