type Tweet = {
  id: number
  postedAt: number
  prev?: Tweet
}

class Twitter {
  private tweets: Map<number, Tweet>
  private follows: Map<number, Set<number>>
  private time = 0

  constructor() {
    this.tweets = new Map()
    this.follows = new Map()
  }

  postTweet(userId: number, tweetId: number): void {
    const tweet: Tweet = {
      id: tweetId,
      postedAt: this.time++,
      prev: this.tweets.get(userId)
    };

    this.tweets.set(userId, tweet)
  }

  getNewsFeed(userId: number): number[] {
    const feed = Array
      .from(this.follows.get(userId) ?? new Set<number>())
      .map(user => this.tweets.get(user))
      .filter((t): t is Tweet => !!t)

	if (this.tweets.has(userId))
      feed.push(this.tweets.get(userId))

    const pq = new MaxPriorityQueue<Tweet>(t => t.postedAt)

    const out: number[] = []
    while (pq.size() > 0 && out.length < 10) {
      const elm = pq.pop()
      out.push(elm.id)
      if (elm.prev) pq.enqueue(elm.prev)
    }

    return out
  }

  follow(followerId: number, followeeId: number): void {
    if (this.follows.has(followerId)) {
      this.follows.get(followerId)!.add(followeeId)
    } else {
      this.follows.set(followerId, new Set([followeeId]))
    }
  }

  unfollow(followerId: number, followeeId: number): void {
    this.follows.get(followerId)!.delete(followeeId)
  }
}
