class Twitter {

    Map<Integer, User> users;
    int timestamp;

    public Twitter() {
        users = new HashMap<>();
        timestamp = 0;
    }

    public void postTweet(int userId, int tweetId) {
        User u = getUser(userId);
        Tweet t = new Tweet(tweetId, userId, ++timestamp);

        Queue<Tweet> userTweets = u.tweets;
        userTweets.offer(t);
        if (userTweets.size() >= 11) {
            userTweets.poll();
        }
    }

    public List<Integer> getNewsFeed(int userId) {
        User u = getUser(userId);
        Queue<Tweet> userTweets = new PriorityQueue<>((a,b) -> (a.timestamp - b.timestamp));
        userTweets.addAll(u.tweets);

        Set<Integer> usersFollowed = u.following;

        for (Integer userFollowed: usersFollowed) {
            Queue<Tweet> followedUserTweets = getUser(userFollowed).tweets;

            Iterator<Tweet> iter = followedUserTweets.iterator();
            while (iter.hasNext()) {
                userTweets.offer(iter.next());
                if (userTweets.size() >= 11) {
                    userTweets.poll();
                }
            }   

        }

        List<Integer> res = new ArrayList<>();
        while (userTweets.size() >= 1) {
            res.add(0,userTweets.poll().tweetId);
        }

        return res;
    }

    public void follow(int followerId, int followeeId) {
        User u = getUser(followerId);
        Set<Integer> followingList = u.following;
        followingList.add(followeeId);
    }

    public void unfollow(int followerId, int followeeId) {
        User u = getUser(followerId);
        Set<Integer> followingList = u.following;

        followingList.remove(followeeId);
    }

    User getUser(int userId) {
        if (!users.containsKey(userId)) {
            users.put(userId, new User(userId));
        }
        return users.get(userId);
    }

