type TweetHeap []*Tweet

func (h TweetHeap) Len() int           { return len(h) }
func (h TweetHeap) Less(i, j int) bool { return h[i].at > h[j].at }
func (h TweetHeap) Swap(i, j int)      { h[i], h[j] = h[j], h[i] }

func (h *TweetHeap) Push(x interface{}) {
	*h = append(*h, x.(*Tweet))
}

func (h *TweetHeap) Pop() interface{} {
	old := *h
	n := len(old)
	x := old[n-1]
	*h = old[0 : n-1]
	return x
}

type Tweet struct {
    id      int
    at      int
    next    *Tweet
}

type Twitter struct {
    follows     map[int]map[int]bool
    tweets      map[int]*Tweet
    time        int
}

func Constructor() Twitter {
    return Twitter{map[int]map[int]bool{}, map[int]*Tweet{}, 0}
}

func (t *Twitter) PostTweet(userId int, tweetId int) {
    t.tweets[userId] = &Tweet{tweetId, t.time, t.tweets[userId]}
    t.time++
}

func (t *Twitter) GetNewsFeed(userId int) []int {
    tweets := TweetHeap{}

    if t.tweets[userId] != nil { heap.Push(&tweets, t.tweets[userId]) }
    for following := range t.follows[userId] {
        if t.tweets[following] != nil { heap.Push(&tweets, t.tweets[following]) }
    }

    feed := []int{}
    for len(feed) < 10 && len(tweets) > 0 {
        mostRecent := heap.Pop(&tweets).(*Tweet)
        if mostRecent.next != nil { heap.Push(&tweets, mostRecent.next) }
        feed = append(feed, mostRecent.id)
    }
    return feed
}

func (t *Twitter) Follow(followerId int, followeeId int)  {
    if t.follows[followerId] == nil { t.follows[followerId] = make(map[int]bool) }
    t.follows[followerId][followeeId] = true
}

func (t *Twitter) Unfollow(followerId int, followeeId int)  {
    if t.follows[followerId] == nil { t.follows[followerId] = make(map[int]bool) }
    delete(t.follows[followerId], followeeId)
}
