from collections import defaultdict
class Twitter:

    def __init__(self):
        self.Time = 0  
        self.users = defaultdict(dict)

    def getTime(self) -> int:
        self.Time += 1
        return 1000000000 - self.Time + 1

    def newUser(self,userId) -> None:
        if self.users.get(userId):
            return
        self.users[userId]["Tweets"] = []
        self.users[userId]["Following"] = [userId]

    def postTweet(self, userId: int, tweetId: int) -> None:
        self.newUser(userId)
        self.users[userId]["Tweets"].append([self.getTime(),tweetId])

    def getNewsFeed(self, userId: int) -> List[int]:
        self.newUser(userId)
        tweets = []
        for user in self.users[userId]["Following"]:
            tweets.extend(self.users[user]["Tweets"])
        tweets.sort()
        n = len(tweets)
        if n>=10:n = 10
        return [tweets[i][1] for i in range(n)]

    def follow(self, followerId: int, followeeId: int) -> None:
        self.newUser(followerId)
        self.newUser(followeeId)
        if followeeId not in self.users[followerId]["Following"]:
            self.users[followerId]["Following"].append(followeeId)

    def unfollow(self, followerId: int, followeeId: int) -> None:
        self.newUser(followerId)
        self.newUser(followeeId)
        if followeeId in self.users[followerId]["Following"]:
            self.users[followerId]["Following"].remove(followeeId)
