from collections import defaultdict


class Twitter:

	# store followers like this: {userId: set(followeeId, ...), ...}
	# twits are stored globally as a list of tuples: (userId, twitId) (stack)
    def __init__(self):
        self.follow_list = defaultdict(set)
        self.twit_storage = []

	# to post twit we just need to append an element to the global stack
    def postTweet(self, userId: int, tweetId: int) -> None:
        self.twit_storage.append((userId, tweetId))

   # firstly construct a set of user's followees ids + user id 
   # iterate over global twit_storage from the end until we find 10 elements or the storage is over
   def getNewsFeed(self, userId: int) -> List[int]:
        user_follows = self.follow_list[userId]
        user_follows.add(userId)
        news = []
        i = len(self.twit_storage) - 1
        while len(news) < 10 and i >= 0:
            if self.twit_storage[i][0] in user_follows:
                news.append(self.twit_storage[i][1])
            i -= 1

        return news

	# add followeeId to user's set in follow list 
    def follow(self, followerId: int, followeeId: int) -> None:
        self.follow_list[followerId].add(followeeId)

	# remove followeeId from user's set in follow list 
    def unfollow(self, followerId: int, followeeId: int) -> None:
        self.follow_list[followerId].discard(followeeId)

