class Twitter {
public:
    vector<unordered_set<int>>connections;
    vector< vector<pair<int,int>> >timeline;// <time,tweetId>
    int time;
    Twitter() {
        connections=vector<unordered_set<int>>(501,unordered_set<int>());
        timeline=vector<vector<pair<int,int>> >(501,vector<pair<int,int> >());
        time=0;
    }

    void postTweet(int userId, int tweetId) {
        timeline[userId].push_back({++time,tweetId});
    }

    vector<int> getNewsFeed(int userId) {
        //make a max_pq according to the tweettime, insert maxtimetweet of all followees of the user ans user into it
        //retrive top 10 tweets;
        vector<int>res;
        priority_queue<vector<int>,vector<vector<int>>,cmp>pq;
        if(timeline[userId].size()>0)
                pq.push({timeline[userId].back().first,timeline[userId].back().second,userId,(int)timeline[userId].size()-1});
        for(auto&con:connections[userId])
        {
            if(timeline[con].size()>0)
                pq.push({timeline[con].back().first,timeline[con].back().second,con,(int)timeline[con].size()-1});
        }
        int k=10;
        while(!pq.empty() && k--)
        {
            auto p=pq.top();
            pq.pop();
            int time=p[0],tweetid=p[1],conid=p[2],tweetnum=p[3];
            res.push_back(tweetid);
            // cout<<conid<<" "<<tweetnum<<" | ";
            tweetnum--;

            if(tweetnum>=0)
            {
                int next_tweettime= timeline[conid][tweetnum].first;
                int next_tweetid= timeline[conid][tweetnum].second;
                pq.push({next_tweettime,next_tweetid,conid,tweetnum});
            }
        }
        // cout<<endl;
        return res;
    }

    void follow(int followerId, int followeeId) {
        connections[followerId].insert(followeeId);
    }

    void unfollow(int followerId, int followeeId) {
        connections[followerId].erase(followeeId);
    }
};

