class Solution {
    fun findContentChildren(need: IntArray, have: IntArray): Int {
        need.sort()
        have.sort()
        var (i, j) = Pair(0, 0)
        while (i < need.size && j < have.size) {
            if (need[i] <= have[j])
                ++i
            ++j
        }
        return i
    }
}
