public int findContentChildren(int[] greed, int[] cookieSize) {
	Arrays.sort(greed);
	Arrays.sort(cookieSize);
	var child = 0;

	for (var cookie = 0; cookie < cookieSize.length && child < greed.length; cookie++)
		if (cookieSize[cookie] >= greed[child])
			child++;

	return child;
}
