var findContentChildren = function(g, s) {
    let count=0;
    let i=0,j=0;
    g.sort((a,b)=>a-b);
    s.sort((a,b)=>a-b);
    while(i<g.length && j<s.length){
        if(g[i]<=s[j]){
		// if the child's greed is less than or equal to cookie's size then we have given that cookie to that child
            count++;
            i++;
            j++;
        }else {
		// if the child's greed is greater than cookie's size then we have to look for next bigger sized cookie(as we have sorted)
            j++;
        }   
    }
    return count;
};
