using System;

public class Solution
{
    public int FindContentChildren(int[] g, int[] s)
    {
        Array.Sort(g);
        Array.Sort(s);

        int childIndex = g.Length - 1;
        int cookieIndex = s.Length - 1;

        int cookiesGiven = 0;
        while (childIndex >= 0 && cookieIndex >= 0)
        {
            if (g[childIndex] > s[cookieIndex])
            {
                childIndex--;
            }
            else
            {
                childIndex--;
                cookieIndex--;
                cookiesGiven++;
            }
        }

        return cookiesGiven;
    }
}
