class Solution {
public:
    int findContentChildren(vector<int>& g, vector<int>& s) {

        int totalChildren = g.size();
        int totalCookies = s.size();


        sort(g.begin(), g.end());
        sort(s.begin(), s.end());

        int currChild = 0;
        int currCookie = 0;

        int totalContentedChildren  = 0;
        while(currChild < totalChildren && currCookie < totalCookies){
            if(s[currCookie] >= g[currChild]){
                totalContentedChildren++;
                currChild++;  // since the current child is contented, move on to the next child
                currCookie++; // the current cookie has been eaten by a child, move on to the next cookie
            }else{
                currCookie++; // try exploring cookies of larger size to satiate the current child
            }
        }

        return totalContentedChildren;

    }
};

```

