class Solution {
public:
    int findContentChildren(vector<int>& g, vector<int>& s) {
        int i=0;
        int j=0;
        int count=0;
// SORT BOTH THE GREED AND SIZE VECTOR
        sort(g.begin(),g.end());
        sort(s.begin(),s.end());
//USING TWO POINTER APPROACH
        while(i<g.size() && j<s.size())
        {
// IF THE GREED AND SIZE OF COOKIE IS SAME THEN ASSIGN THE COOKIE
            if(g[i]==s[j])
            {
                count++;
                i++;
                j++;
            }

            else if(g[i]<s[j])
            {
                count++;
                i++;
                j++;
            }
            else
            {
                j++;
            }
        }
        return count;
    }

};
