class Solution {
    func findContentChildren(_ g: [Int], _ s: [Int]) -> Int {

        let ch = g.sorted()
        let co = s.sorted()

        var chi = 0
        var coi = 0
        var res = 0

        while chi < ch.count, coi < co.count {
            if ch[chi] <= co[coi] {
                res += 1
                chi += 1
            }
            coi += 1
        }

        return res
    }
}
