var findContentChildren = function(g, s) {
    g = g.sort((a, b) => a - b);
    s = s.sort((a, b) => a - b);
    let cursorG = 0;
    let cursorS = 0;
    let output = 0;

    while (cursorS <= s.length && cursorG <= g.length) {
        if (s[cursorS] >= g[cursorG]) {
            cursorS++;
            cursorG++;
            output++;
        } else {
            cursorS++;
        }
    }

    return output;
};
