class Solution {
    public int findContentChildren(int[] g, int[] s) {
        int i=0,j=0,n1=g.length,n2=s.length,count=0;
        Arrays.sort(g);
        Arrays.sort(s);
        while(i<n1 && j<n2){
            int x = g[i], y= s[i]; 
            if(s[j]>=g[i]){
                count++;
                j++;
                i++;
            }else if(x>y){
                j++;
            }else{
                i++;
            }
        }
        return count;
    }
}
