import java.util.*;
import java.util.stream.*;

public class Solution {
    private static class PurePartition {
        public static PurePartition head;
        public static PurePartition tail;

        public MixedPartition previous;
        public MixedPartition next;
        public int unassignedG;
        public int unassignedS;
        public int c;
        public int leftPriority;
        public int rightPriority;

        public PurePartition() {
            this.unassignedG = 0;
            this.unassignedS = 0;
            this.c = 0;
        }

        public void leftInsertionSort() {
            while (this.previous != null && this.unassignedS >= this.previous.g.size()) {
                this.unassignedS += this.previous.s.size() - this.previous.g.size()
                        + this.previous.previous.unassignedS - this.previous.previous.unassignedG;
                this.c += this.previous.g.size() + this.previous.previous.unassignedG
                        + this.previous.previous.c;
                if (this.previous.leftChild != null) {
                    this.previous.leftChild.parent = this.previous.parent;
                }
                if (this.previous.parent.leftChild == this.previous) {
                    this.previous.parent.leftChild = this.previous.leftChild;
                } else if (this.previous.parent.rightChild == this.previous) {
                    this.previous.parent.rightChild = this.previous.leftChild;
                }
                this.previous = this.previous.previous.previous;
            }
            if (this.previous == null) {
                PurePartition.head = this;
            } else {
                this.previous.next = this;
            }
        }

        public void rightInsertionSort() {
            while (this.next != null && this.unassignedG >= this.next.s.size()) {
                this.unassignedG += this.next.g.size() - this.next.s.size()
                        + this.next.next.unassignedG - this.next.next.unassignedS;
                this.c += this.next.s.size() + this.next.next.unassignedS
                        + this.next.next.c;
                if (this.next.rightChild != null) {
                    this.next.rightChild.parent = this.next.parent;
                }
                if (this.next.parent.rightChild == this.next) {
                    this.next.parent.rightChild = this.next.rightChild;
                } else if (this.next.parent.leftChild == this.next) {
                    this.next.parent.leftChild = this.next.rightChild;
                }
                this.next = this.next.next.next;
            }
            if (this.next == null) {
                PurePartition.tail = this;
            } else {
                this.next.previous = this;
            }
        }
    }

    private static class MixedPartition {
        public static MixedPartition root;

        public static void heapSort() {
            while (MixedPartition.root != null) {
                MixedPartition.root.quickSort();
            }
        }

        public static MixedPartition mergeSort(MixedPartition left, MixedPartition right) {
            MixedPartition head = null;
            MixedPartition tail = null;
            int direction = 0;
            while (true) {
                if (direction == 0) {
                    if (left == null && right == null) {
                        return null;
                    } else if (left == null) {
                        head = right;
                        break;
                    } else if (right == null) {
                        head = left;
                        break;
                    } else if (left.priority >= right.priority) {
                        head = tail = left;
                        left = left.rightChild;
                        direction = 1;
                    } else if (left.priority < right.priority) {
                        head = tail = right;
                        right = right.leftChild;
                        direction = 2;
                    }
                } else if (direction == 1) {
                    if (left == null) {
                        right.parent = tail;
                        tail.rightChild = right;
                        break;
                    } else if (left.priority >= right.priority) {
                        left.parent = tail;
                        tail = tail.rightChild = left;
                        left = left.rightChild;
                        direction = 1;
                    } else if (left.priority < right.priority) {
                        right.parent = tail;
                        tail = tail.rightChild = right;
                        right = right.leftChild;
                        direction = 2;
                    }
                } else if (direction == 2) {
                    if (right == null) {
                        left.parent = tail;
                        tail.leftChild = left;
                        break;
                    } else if (left.priority >= right.priority) {
                        left.parent = tail;
                        tail = tail.leftChild = left;
                        left = left.rightChild;
                        direction = 1;
                    } else if (left.priority < right.priority) {
                        right.parent = tail;
                        tail = tail.leftChild = right;
                        right = right.leftChild;
                        direction = 2;
                    }
                }
            }
            head.parent = null;
            return head;
        }

        public PurePartition previous;
        public PurePartition next;
        public MixedPartition parent;
        public MixedPartition leftChild;
        public MixedPartition rightChild;
        public ArrayList<Integer> g;
        public ArrayList<Integer> s;
        public int priority;

        public MixedPartition() {
            this.g = new ArrayList<Integer>();
            this.s = new ArrayList<Integer>();
        }

        public MixedPartition(ArrayList<Integer> g, ArrayList<Integer> s) {
            this.g = g;
            this.s = s;
        }

        public void quickSort() {
            MixedPartition left = new MixedPartition();
            PurePartition middle = new PurePartition();
            MixedPartition right = new MixedPartition();

            Integer pivot;
            if (this.g.size() <= this.s.size()) {
                pivot = this.g.get((new Random()).nextInt(this.g.size()));
            } else {
                pivot = this.s.get((new Random()).nextInt(this.s.size()));
            }

            for (Integer i : this.g) {
                if (i.compareTo(pivot) < 0) {
                    left.g.add(i);
                } else if (i.compareTo(pivot) == 0) {
                    ++middle.unassignedG;
                } else {
                    right.g.add(i);
                }
            }
            for (Integer i : this.s) {
                if (i.compareTo(pivot) < 0) {
                    left.s.add(i);
                } else if (i.compareTo(pivot) == 0) {
                    ++middle.unassignedS;
                } else {
                    right.s.add(i);
                }
            }

            middle.c = Math.min(middle.unassignedG, middle.unassignedS);
            middle.unassignedG -= middle.c;
            middle.unassignedS -= middle.c;

            if (this.previous.unassignedG < left.s.size()
                    && left.g.size() > middle.unassignedS
                    && middle.unassignedG < right.s.size()
                    && right.g.size() > this.next.unassignedS) {
                this.previous.next = left;
                left.previous = this.previous;
                left.next = middle;
                middle.previous = left;
                middle.next = right;
                right.previous = middle;
                right.next = this.next;
                this.next.previous = right;
                middle.leftPriority = middle.next.next.leftPriority - middle.next.next.unassignedS
                        + middle.next.next.unassignedG - middle.next.s.size() + middle.next.g.size();
                middle.rightPriority = middle.previous.previous.rightPriority - middle.previous.previous.unassignedG
                        + middle.previous.previous.unassignedS - middle.previous.g.size() + middle.previous.s.size();
                left.priority = Math.max(left.previous.leftPriority + left.s.size(), left.next.rightPriority + left.g.size());
                right.priority = Math.max(right.previous.leftPriority + right.s.size(), right.next.rightPriority + right.g.size());
                MixedPartition.root = MixedPartition.mergeSort(MixedPartition.mergeSort(this.leftChild, MixedPartition.mergeSort(left,
                        right)), this.rightChild);
            } else if ((this.previous.unassignedG >= left.s.size() || left.g.size() <= middle.unassignedS)
                    && this.previous.unassignedG + left.g.size() < left.s.size() + middle.unassignedS
                    && middle.unassignedG < right.s.size()
                    && right.g.size() > this.next.unassignedS) {
                if (this.previous.previous != null) {
                    this.previous.previous.next = middle;
                }
                middle.previous = this.previous.previous;
                middle.next = right;
                right.previous = middle;
                right.next = this.next;
                this.next.previous = right;
                middle.unassignedS += left.s.size() - left.g.size() + this.previous.unassignedS - this.previous.unassignedG;
                middle.c += left.g.size() + this.previous.unassignedG + this.previous.c;
                middle.leftInsertionSort();
                middle.leftPriority = middle.next.next.leftPriority - middle.next.next.unassignedS
                        + middle.next.next.unassignedG - middle.next.s.size() + middle.next.g.size();
                if (middle.previous != null) {
                    middle.rightPriority = middle.previous.previous.rightPriority - middle.previous.previous.unassignedG
                            + middle.previous.previous.unassignedS - middle.previous.g.size() + middle.previous.s.size();
                }
                right.priority = Math.max(right.previous.leftPriority + right.s.size(), right.next.rightPriority + right.g.size());
                MixedPartition.root = MixedPartition.mergeSort(MixedPartition.mergeSort(this.leftChild, right), this.rightChild);
            } else if ((middle.unassignedG >= right.s.size() || right.g.size() <= this.next.unassignedS)
