				//cout << "Checking child #" << j << " with greed " << g[j] << endl;

				if  (receivers.find(j) == receivers.end() && s[i] >= g[j])
				{
					//cout << "Added #" << j << endl;
					receivers.insert(j);
					break;
				}
			}
		}

		return receivers.size();
	}
