    def isPossible(self, nums: List[int]) -> bool:
        available = Counter(nums)
        vacancy = defaultdict(int)

        for num in nums:
            if not available[num]:
                continue
            elif vacancy[num]:
                vacancy[num]-=1
                available[num]-=1
                vacancy[num+1]+=1
            elif available[num+1] and available[num+2]:
                available[num]-=1
                available[num+1]-=1
                available[num+2]-=1
                vacancy[num+3]+=1
            else:
                return False

        return True

