        TreeMap<Integer, Integer> map = new TreeMap<>();
        for(int num: nums) map.put(num, map.getOrDefault(num, 0) + 1);

        while(map.size() > 0) {
            int len = 0;
            int curr = map.firstKey(); //pick the smallest element
            while(true) {
                int count = map.get(curr);
                if(count == 1) map.remove(curr);
                else map.put(curr, count - 1);
                curr++;
                len++;
				//if next element is not consecutive or next element frequence is smaller than current one.
				//e.g {1,2,3,3,4,5} 3 has 2 freqs, and 4 has 1 freq. So, current subseqence should stop at 3.
                if(!map.containsKey(curr) || map.get(curr) < count) break; 
            }
            if(len < 3) return false;
        }

        return true;
    }
