class Solution:
    def isPossible(self, nums: List[int]) -> bool:
        counts = collections.Counter(nums)
        endsAt = collections.Counter()
        for n in nums:
            if counts[n] > 0 :
                if endsAt[n - 1] > 0:
                    endsAt[n - 1] -= 1
                    endsAt[n] += 1
                elif counts[n + 1] > 0 and counts[n + 2] > 0:
                    endsAt[n + 2] += 1
                    counts[n + 1] -= 1
                    counts[n + 2] -= 1
                else:
                    return False
                counts[n] -= 1
        return True
