        for (int i : nums) {
            if (l1.find(i - 1) != l1.end()) {
                l1.erase(l1.find(i - 1)), l2.insert(i);
            } else if (l2.find(i - 1) != l2.end()) {
                l2.erase(l2.find(i - 1)), l3.insert(i);
