            freqMap.set(i+2, freqMap.get(i+2) - 1);
            appendfreq.set(i+3, (appendfreq.get(i+3) || 0) + 1);
        }
        else return false;
        freqMap.set(i, freqMap.get(i) - 1);
    }
    return true;
};
