        } else {
            while(dif < 0) {
                if (queue.isEmpty() || timestamp - queue.peek()[0] < 3) {
                    return false;
                } else if (queue.peek()[1] + dif > 0) {
                    queue.peek()[1] += dif;
                    break;
                } else {
                    dif += queue.poll()[1];
                }
            }
        }
        return true;
    }
}
