class Solution:
    def isPossible(self, nums: List[int]) -> bool:
        def gen_prefix_list() -> list[int]:
            return [0, 0, 0]

        prefixes: dict[int, list[int]] = defaultdict(lambda: gen_prefix_list())

        for n in nums:
            prefix = prefixes[n]

            if n-1 in prefixes:
                prev_prefix = prefixes[n-1]                
                if prev_prefix[0]:
                    prev_prefix[0] -= 1
                    prefix[1] += 1
                elif prev_prefix[1]:
                    prev_prefix[1] -= 1
                    prefix[2] += 1
                elif prev_prefix[2]:
                    prev_prefix[2] -= 1
                    prefix[2] += 1
                if prev_prefix[0] == prev_prefix[1] == prev_prefix[2] == 0:
                    prefixes.pop(n-1)
            else:
                prefix[0] += 1
        return all(p[0] == p[1] == 0 for p in prefixes.values())
