class Solution {
public:
    bool isPossible(vector<int>& nums) {
        //map to store the frequencies
        vector<int> freq(2004,0);
        for(int i=0;i<nums.size();i++)
            freq[nums[i]+1001]++; //for the negative values

        vector<int> mp(2004,0);//contains the subsequences endings
        for(int i=0;i<nums.size();i++)
        {
            //make sure the element is not  used 
            if(freq[nums[i]+1001]>0)
            {
                if(mp[nums[i]+1000]>0)
                {
                    //if there is a subsequence eding with nums-1 attach  to it
                    mp[nums[i]+1000]--;
                    mp[nums[i]+1001]++;
                }
                else{
                    //if no subsequence then create one
                    if(freq[nums[i]+1+1001]<=0 || freq[nums[i]+1001+2]<=0)
                        return false; //we can't split
                    freq[nums[i]+1+1001]--;
                    freq[nums[i]+2+1001]--;
                    mp[nums[i]+2+1001]++;
                }
                //as we have used this one
                freq[nums[i]+1001]--;
            }
        }
        return true;
    }
};
