# N^2, use a stack to hold consecutive sequences, iterate all sequences to insert current number n.
class Solution:
    def isPossible(self, nums: List[int]) -> bool:
        stack = []
        for n in nums:
            good = False
            for i in range(len(stack)-1, -1, -1):
                if stack[i][1] == n - 1:
                    stack[i][1] = n
                    good = True
                    break
            if not good:
                stack.append([n,n])
        for s in stack:
            if s[1] - s[0] < 2:
                return False
        return True

# NlogN, use a heap to store consecutive sequences. heap top has the smallest end and shortest length of sequences.
class Solution:
    def isPossible(self, nums: List[int]) -> bool:
        hp = []
        for n in nums:
            if not hp:
                heapq.heappush(hp, (n, 1))
            elif hp[0][0] == n - 1:
                _, l = heapq.heappop(hp)
                heapq.heappush(hp, (n, l+1))
            elif hp[0][0] == n:
                heapq.heappush(hp, (n,1))
            else:
                while hp and hp[0][0] < n - 1:
                    _, l = heapq.heappop(hp)
                    if l < 3:
                        return False
                if hp and hp[0][0] == n - 1:
                    _, l = heapq.heappop(hp)
                    heapq.heappush(hp, (n, l+1))
                else:
                    heapq.heappush(hp, (n,1))
        for h in hp:
            if h[1] < 3:
                return False
        return True

# O(N), create two dicts, one for pending sequences with length less than 3 and good_sequences with length >= 3.
# Try to add current n to an existing pending sequence or good_sequences of n-1, If neither, create a new pending sequence of n.
class Solution:
    def isPossible(self, nums: List[int]) -> bool:
        good_seq = {}
        pending = collections.defaultdict(list)
        for n in nums:
            if pending[n-1]:
                l = pending[n-1].pop()
                if l == 2:
                    good_seq[n] = good_seq.get(n, 0) + 1
                else:
                    pending[n].append(l+1)
            elif good_seq.get(n-1, 0):
                good_seq[n-1] -= 1
                good_seq[n] = good_seq.get(n, 0) + 1
            else:
                pending[n].append(1)

        for p in pending.values():
            if p:
                return False
        return True
```

