    bool isPossible(vector<int>& a) {
        int j = 0, n = a.size(), cnt1=0,cnt2=0,cnt3=0;
        for (int i = 0; i < n; ++i) if(i == n-1 || a[i] != a[i+1]) {
            int cnt = i-j+1;
            if (j == 0 || a[i] > a[j-1] + 1) {
                if (cnt1 || cnt2) return false;
                cnt2=cnt3 = 0;
                cnt1 = cnt;
            }
            else {
                if (cnt < cnt1 + cnt2) return false;
                cnt -= cnt1 + cnt2;
                int x = min(cnt,cnt3);
                cnt3 = x + cnt2, cnt2 = cnt1, cnt1 = cnt - x;
            }
            j = i + 1;
        }

        return !cnt2 && !cnt1;
    }
