class Solution {
public:
    bool isPossible(vector<int>& nums) {
        unordered_map<int,int> m;
        // count the frequency for each number
        for(int x: nums) m[x]++;
        // use 3 variables to store
        // a: subsequences of length 1
        // b: subsequences of length 2
        // c: subsequences of length 3 or more
        int a = 0, b = 0, c = 0;
        // iterate all possible number
        for(int k = -1000; k <= 1001; k++){
            // since we need to put k to all subsequences of length 1 and 2
            // we need at least a + b numbers
            // otherwise, it is false
            if(m[k] < a + b) return false;
            // check if we need to create a new subsequence after extending to a, b, and c
            // we only do it when m[k] - a - b - c > 0
            int s = max(0, m[k] - a - b - c);
            // previous subsequences of length 2 &  3
            c = m[k] - s - a;
            // append the current number k to a
            // old_a would become subsequences of length 2 which is b
            b = a;
            // s is the new subsequence of length 1
            a = s;
        }
        return true;
    }
};
