class Solution {
public:
    struct compare{
        bool operator()(vector<int> &a, vector<int> &b){
            if(a[1] == b[1]) {
                int l1 = a[1] - a[0] + 1;
                int l2 = b[1] - b[0] + 1;
                return l1>l2;
            }
            return a[1]>b[1];
        }
    };
    bool isPossible(vector<int>& nums) {
        priority_queue<vector<int>, vector<vector<int>>, compare> pq;
        for(auto i: nums) {
            while(pq.size() > 0 && (pq.top()[1] + 1) < i) {
                vector<int> top = pq.top();
                pq.pop();
                if((top[1] - top[0] + 1) < 3) {
                    // cout<<"here";
