class Solution {
    public boolean isPossible(int[] nums) {
        /*
          we will take two hashMap.
          1. elementFreq -> it will contain the freq of element.
          2. endOfSubsequence -> it will store the num where sequence is ending.
        */

        Map<Integer,Integer> elementFreq = new HashMap<>();
        Map<Integer,Integer> endOfSubsequence = new HashMap<>();

        for(int num:nums){
            elementFreq.put(num, elementFreq.getOrDefault(num,0)+1);
        }

        for(int num:nums){
             // it means this element has been already used in subsequence
             if(elementFreq.get(num)==0) continue;
             elementFreq.put(num,elementFreq.getOrDefault(num,0)-1);

             if(endOfSubsequence.getOrDefault(num-1,0)>0){
                 // Here we are increasing the length of existing subsequence
                 endOfSubsequence.put(num-1, endOfSubsequence.get(num-1)-1);
                 endOfSubsequence.put(num, endOfSubsequence.getOrDefault(num,0)+1);

             }  else if(elementFreq.getOrDefault(num+1,0)>0 && elementFreq.getOrDefault(num+2,0)>0){
                 // we are making num+2 as ending of subsequence
                 elementFreq.put(num+1,elementFreq.getOrDefault(num+1,0)-1);
                 elementFreq.put(num+2,elementFreq.getOrDefault(num+2,0)-1);
                 endOfSubsequence.put(num+2, endOfSubsequence.getOrDefault(num+2,0)+1);

             }  else {
                 return false;
             }        

        }

        return true;

    }
}
