// 2 HashMap Solution
// Time complexity: O(N)
// Space complexity: O(N)
class Solution {
    public boolean isPossible(int[] nums) {
        if (nums == null || nums.length < 3) return false;
        Map<Integer, Integer> freqMap = new HashMap<>();  // Store <num, freq> pair
        Map<Integer, Integer> seqMap = new HashMap<>();  // Store <the end num, freq> pair
        for (int num : nums) {
            freqMap.put(num, freqMap.getOrDefault(num, 0) + 1);
        }
        for (int num : nums) {
            int freq = freqMap.get(num);
            if (freq == 0) {
                // If the num freq is 0, num has been added to one subsequence.
                continue;
            } else if (seqMap.getOrDefault(num - 1, 0) > 0) { 
                // If num could be add into one subsequnce, update seqMap.
                seqMap.put(num - 1, seqMap.get(num - 1) - 1);
                seqMap.put(num, seqMap.getOrDefault(num, 0) + 1);
            } else if (freqMap.getOrDefault(num + 1, 0) > 0 && freqMap.getOrDefault(num + 2, 0) > 0) {
                // If num could be formed as one subsequnce, add it to seqMap.
                freqMap.put(num + 1, freqMap.get(num + 1) - 1);
                freqMap.put(num + 2, freqMap.get(num + 2) - 1);
                seqMap.put(num + 2, seqMap.getOrDefault(num + 2, 0) + 1);
            } else {
                return false;
            }
            freqMap.put(num, freq - 1);
        }
        return true;
    }
}
