class Solution {
public:
    bool isPossible(vector<int>& nums) 
    {
        int n=nums.size();
        if(n<3)return false;
        unordered_map<int,int>hash;
        for(int i=0;i<n;i++)hash[nums[i]]++;
        int count=n;
        for(int i=0;i<n;i++)
        {
            int x=nums[i];
            if(hash[x]>0 and hash[x+1]>0 and hash[x+2]>0)
            {
                hash[x]--;
                hash[x+1]--;
                hash[x+2]--;
                x=x+3;
                count=count-3;
                while(hash[x]>0 and hash[x]>hash[x-1])
                {
                    count--;
                    hash[x]--;
                    x++;
                }
            }
        }
        if(count==0)return true;
        else return false;


    }
};
