```rust
use std::collections::HashMap;

impl Solution {
    pub fn is_possible(nums: Vec<i32>) -> bool {
        let mut count: HashMap<i32, i32> = HashMap::new();
        let mut subseq: HashMap<i32, i32> = HashMap::new();

        let min = *nums.iter().min().unwrap();
        let max = *nums.iter().max().unwrap();

        for i in min..max+2 { count.insert(i, 0); }
        for i in min-1..max+1 { subseq.insert(i, 0); }

        for n in nums.iter() {
            let val = count.get(&n).unwrap();
            count.insert(*n, val + 1);
        }

        for n in nums.iter() {
            if *count.get(n).unwrap() == 0 { continue; }
            count.insert(*n, *count.get(n).unwrap() - 1);

            if *subseq.get(&(n - 1)).unwrap() > 0 {
                subseq.insert((n - 1), *subseq.get(&(n - 1)).unwrap() - 1);
                subseq.insert(*n, *subseq.get(&n).unwrap() + 1);
            } else if *count.get(&(n + 1)).unwrap() > 0 && *count.get(&(n + 2)).unwrap() > 0 {
                count.insert((n + 1), *count.get(&(n + 1)).unwrap() - 1);
                count.insert((n + 2), *count.get(&(n + 2)).unwrap() - 1);
                subseq.insert((n + 2), *subseq.get(&(n + 2)).unwrap() + 1);
            } else {
                return false;
            } 
        }

        return true;
    }
}
