```
class Solution {
    public boolean isPossible(int[] nums) {
        List<List<Integer>> lists = new ArrayList<>();
        int completedList = 0;

        for (int i = 0; i < nums.length; i++) {
            // For each word, keep going back all the lists and see if you can add. If you can't, only then create one
            boolean found = false;
            int num = nums[i];
            int index = lists.size() - 1;
            while (index >= 0) {
                List<Integer> list = lists.get(index);
                if (list.get(list.size() - 1) == nums[i] - 1) {
                    found = true;
                    list.add(nums[i]);
                    break;
                } else if (list.get(list.size() -1)  < nums[i] - 1) {
                    if (list.size() < 3) {
                        return false;
                    }
                    break;
                }
                index--;
            }

            if (!found) {
                List<Integer> list = new ArrayList<>();
                list.add(nums[i]);
                lists.add(list);
            }


        }

        for (int i = 0; i < lists.size(); i++) {
            if (lists.get(i).size() < 3) {
                return false;
            }
        }

        return true;
    }
}
