class Solution {
    public boolean isPossible(int[] nums) {
        int n=nums.length;
        if(n<3) return false;

        //use map to record ending element - length pair
        HashMap<Integer,PriorityQueue<Integer>> map=new HashMap<>();
        for(int i=0;i<n;i++){
            if(map.containsKey(nums[i]-1)){
                PriorityQueue<Integer> pq1=map.get(nums[i]-1);
                int len=pq1.poll();
                if(pq1.isEmpty()) map.remove(nums[i]-1);
                len++;
                PriorityQueue<Integer> pq2=map.getOrDefault(nums[i], new PriorityQueue<Integer>());
                pq2.add(len);
                map.put(nums[i],pq2);
            }
            else{
                //start a new sequence
                PriorityQueue<Integer> pq3= map.getOrDefault(nums[i],new PriorityQueue<Integer>());
                pq3.add(1);
                map.put(nums[i],pq3);
            }

            if(i<n-1 && nums[i+1]!=nums[i] ){
                 //the last sequence that has not been extended to nums[i] will not be continued. check length
                if(map.containsKey(nums[i]-1) && map.get(nums[i]-1).peek()<3){
                    return false;
