class Solution {
public:
    bool isPossible(vector<int>& nums) {

        unordered_map<int, int> freq, need;

        for (const auto & n : nums)
        {
            freq[n]++;
        }

        for (const auto & num : nums)
        {
            if (freq[num] == 0) continue;

            if (need[num] > 0)
            {
                need[num]--;
                need[num+1]++;
            }
            else if (freq[num+1] > 0 && freq[num+2] > 0)
            {
                freq[num+1]--;
                freq[num+2]--;

                need[num+3]++;
            }
            else
                return false;

            freq[num]--;
        }

        return true;
    }
};
