class Solution {
    public boolean isPossible(int[] nums) {
        if (nums == null || nums.length < 3) {
            return false;
        }
        HashMap<Integer, Integer> requirements = new HashMap<>();
        HashMap<Integer, Integer> counts = new HashMap<>();
        for (int i = 0; i < nums.length; i++) {
            int num = nums[i];
            int pre = counts.getOrDefault(num-1, 0);
            int now = counts.getOrDefault(num, 0);
            if (pre <= now) { //starter
                requirements.put(num+1, requirements.getOrDefault(num+1, 0) + 1);
                requirements.put(num+2, requirements.getOrDefault(num+2, 0) + 1);
            }
            if (requirements.getOrDefault(num, 0) > 0) {
                requirements.put(num, requirements.get(num) - 1);
            }
            counts.put(num, counts.getOrDefault(num, 0) + 1);
        }
        for (int num: requirements.keySet())  {
            if (requirements.get(num) > 0) {
                return false;
            }
        }
        return true;
    }
}
