                if(count > all_seq){
                    seq += seq2;
                    seq2 = seq1;
                    seq1 = count - all_seq;
                }else if(count >= seq1 + seq2){ // Current number has enough items to lengthen all incomplete sequences (length == 1 or 2)                    
                    seq = count - seq1;
                    seq2 = seq1;
                    seq1 = 0;
                }else 
