result NUMBER;
x Employee.salary%TYPE;
CURSOR cur IS SELECT DISTINCT(salary) FROM Employee ORDER BY salary DESC;
BEGIN
    OPEN cur;
    LOOP
        FETCH cur INTO x;
        EXIT WHEN cur%ROWCOUNT>=N OR cur%NOTFOUND;
    END LOOP;
    IF cur%ROWCOUNT=N THEN
        RETURN x;
    END IF;
    CLOSE cur;
    RETURN NULL;
END;
